let [todos, setTodos] = useState([]);
let [todo, setTodo] = useState("");


let addTodo =()=> {
  if(todo !== '') {
    setTodos([...todos, todo]);
    setTodo("");
  }
};

let deleteTodo =(text)=> {
  let newTodos = todos.filter((todo)=> {
    return todo !== text
  })
  setTodos(newTodos);
}

