        // hard
let addTodo =()=> {
  if(todo !== '') {
    setTodos([...todos, todo]);
    setTodo("");
  }
}
        // hard
let deleteToDo =(text)=> {
  let newTodos = todos.filter((todo)=> {
    return todo !== text
  })
  setTodos(newTodos);
}

  return(
    <div className='App'>
        <h1>Create new ToDo</h1>

    </div>
  )
}


