    let [states, setStates] = useState([]);

let addToState = ()=> {
  if(state !== '') {
    setStates()
  }
}

    return (

      <div className='App'>
        <h1>To-Do List</h1>
        <div className='input-wrapper'>
          <input 
                type='text'
                value={state}
                name='todo'
                placeholder='please write...'
                onChange={(e)=> setState(e.target.value)}
              >
            </input>
                <button 
                  className='add-button'

                >Add</button>
        </div>

      </div>
    )
  }


  export default App





























