import './App.css';
import { useState } from 'react'; 

function App() {

  let [todo, setTodo] = useState("");
  let [todos, setTodos] = useState([]);

  let addToDo = ()=> {
      if(todo !== '') {
        setTodos([...todos, todo]);
        setTodo("");
      }
  }

  let deleteButton = (text) => {
      let newTodos = todos.filter((todo)=> {
          return todo !== text;
      })
      setTodos(newTodos);
  }

  return (
