        import React from 'react';
        import '../App.css';




    function TodoInputs() {
        return (
                <div>  
                    <input
                type='text'
                value={todo}
                placeholder='Введіть значення'
                name='todo'
                onChange={(e)=> {setTodo(e.target.value)}}
            />
            <button 
                className='add-button'
                onClick = {addTodo}
            >Додати</button></div>
        )
        }
        
    export default TodoInputs;
    
    
    
    
    
    
    
    
    
    
    
    
    
    
    
    
