import React from 'react'
import { useState } from 'react'
import './App.css';

  function App() {

    let [state, setState] = useState('');
    let [states, setStates] = useState([]);

let addToState = ()=> {
  if(state !== '') {
    setStates([...states, state])
    setState('');
  }
}

let deleteTodo =(text)=> {
  let newTodos = states.filter((state)=> {
    return state !== text;
  });
  setStates(newTodos);
}

    return (

      <div className='App'>
        <h1>To-Do List</h1>
        <div className='input-wrapper'>
          <input 
                type='text'
                value={state}
                name='todo'
                placeholder='please write...'
                onChange={(e)=> setState(e.target.value)}
              >
            </input>
                <button 
                  className='add-button'
                  onClick={addToState}
                >Add</button>
        </div>
        {states?.length > 0 ? (
          <ul className='todo-list'>
              {states.map((state, index)=> (
                <div className='todo'>
                    <li key={index}>{state}</li>
                    <button className ='delete-button'
                      onClick={()=> deleteTodo(state)}
                    >Delete</button>
                </div>
              ))}
          </ul>
  ) : (
  <div className='empty'>
    <p>No found</p>
  </div>
)}
  </div>
    )}



  export default App;





























