let TodoInput = ({todo, setTodo, addTodo}) => {
    return(
        <div className='input-wrapper'>
        <input
        type='text'
        name='todo'
        placeholder='create new toDo'
        value={todo}
        onChange={(e)=>setTodo(e.target.value)}
        />
        <button className='add-button'
        onClick={addTodo}
        // hard
        >Add</button>
        </div>
    )
}
    export default TodoInput;
