import './App.css';
import { useState } from 'react';
import React from 'react'
import TodoInputs from './components/TodoInputs';
import TodoList from './components/TodoList';

function App() {

  let [todo, setTodo] = useState("");
  let [todos, setTodos] = useState([]);

  let addTodo = ()=> {
    if(todo !== "") {
      setTodos([...todos, todo]);
      setTodo("");
    }
  }

  let deleteTodo = (text)=> {
      let newTodos = todos.filter((todo)=> {
          return todo !== text;
      });
      setTodos(newTodos);
  } 

  return (

    <div className='App'>
      <h1>To Do List</h1>
        <TodoInputs addTodo={addTodo} setTodo={setTodo} todo={todo}/>
        <TodoList remove={deleteTodo} list={todos}/>
    </div>
  )
}

export default App;

























