import './App.css';
import { useState } from 'react';

let App =()=> {

let [todo, setTodo] = useState("");
let [todos, setTodos] = useState([]);

let addAll =()=> {
  setAll([...all, all])
}

let addTodo =()=> {
  if(todo !== '') {
    setTodos([...todos, todo]);
    setTodo("");
  }
}

let to

  return(
    <div className='App'>
        <h1>Create new ToDo</h1>
        <div className='input-wrapper'>
              <input
                type='text'
                name='todo'
                placeholder='create new toDo'
                value={todo}
                onChange={(e)=>setTodo(e.target.value)}
              />
              <button className='add-button'
                onClick={addTodo}
              // hard
              >Add</button>
              {todos?.length > 0 ? (
              <ul className='todo-list'>
                  {todos.map((todo, index)=> (
                    <div className='todo'>
                      <li key={index}>{todo}</li>
                      <button className='delete-button'>Delate</button>
                    </div>
                  ))}

              </ul>
              ): (
                <div className='empty'>
                  <p>No task found</p>
                </div>
              )} 
        </div>
    </div>
  )
}


export default App;
