import React from 'react';
import './App.css';

import { useState } from 'react'; 

function App() {

  let [todo, setTodo] = useState("");
  let [todos, setTodos] = useState([]);

  let addToDo = ()=> {
      if(todo !== '') {
        setTodos([...todos, todo]);
        setTodo("");
      }
  }

  let deleteButton = (text) => {
      let newTodos = todos.filter((todo)=> {
          return todo !== text;

      })
      setTodos(newTodos);
  }


  return (
    <div className='App'>
      <h1>To-Do List</h1>
      <div className='input-wrapper'>
          <input 
            type='text'
            value={todo}
            placeholder='Введіть щось'
            onChange={(e)=>setTodo(e.target.value)}
          />
          <button className='add-button'
              onClick={addToDo}
          >
            Натисніть
          </button>

      {todos?.length > 0 ? (
          <ul className='todo-list'>
            {todos.map((todo, index)=> ( 
              <div className='todo'>
                    <li key={index}>{todo}</li>
                    <button 
                      className='delete-button'
                      onClick={()=> {deleteButton(todo)
                      }}
                      >Видалити</button> 
                </div>
                ))}
          </ul>
          ) : (
                <div className='empty'>
                <p>Відсутній запис</p>
              </div>
          )}
      </div>

    </div>
  )
}
export default App;

























