import React from 'react'
import './App.css';


function TodoInput({}) {
    return (
        <>
            <iput 
                type='text'
                value={todo}
                placeholder='Введіть щось'
                onChange={(e)=>setTodo(e.target.value)}
            />
            <button className='add-button'
                onClick={addToDo}
            >Натисніть 
            </button>
        </>
    )
}

export default TodoInput;
